net.rptools.maptool.model.MacroButtonPro.command:
[ dice = arg( 0 ) ]
[ sides = arg( 1 ) ]
[ roll = 0 ]
[ total = 0]
[ cntpens = 0 ]
[if(argCount()==3), code:
{
	[pen = arg(2)]
};
{
	[pen = sides]
}]

<!--Check for Lockup Loop Illegal Pen Value-->
[abort(if(pen<=1,0,-1))]

<!-- Explode Dice if they roll equal or greater than target number. -->
[for(i,0,dice,1,""), code:
{
	[roll=eval("1d"+sides)]
	[total=total+roll]
	[while(roll>=pen), code:
	{
		[roll = if(sides==20,1d6,if(sides == 100, 1d20, eval("1d"+sides) ) )]
		[total=total+roll-1]
		[ cntpens = cntpens+1 ]
	}]

}]

[ macro.return = total ]

[h, if(macro.args != ""): switchToken(macro.args);""]
[h: TempWounds="[]"]
[h: TotalSev=0]
[h, foreach(curwound, Wounds, ","), code:
{
	[curSev=json.get(curwound,"Sev")]
	[if(curSev>0), code:
	{
		[TotalSev=TotalSev+curSev]
		[TempWounds = json.append(TempWounds,curwound)]		
	};
	{
	}]
}]
[h: Wounds=TempWounds]
[h: HP=MaxHP-TotalSev]

[h: assert(hasImpersonated(),"ERROR: You Must Impersonate to Display a Character Sheet", 0)]
[h: varsFromStrProp(SheetDetails)]

[h: Changed=0]

[frame("Character Sheet"):
{
	[h: SelectedCount = listCount(getSelected())]
	[h: assert(SelectedCount  <= 1,"ERROR: Error Diplaying Character Sheet. More Than One Token Selected",0)]

	[r, switch(Sheet), code:
	case "Particulars":
	{
		[r, macro("HTML_CharSheet_Partic@Lib:HackMaster"): macro.args]

		[h: SheetDetails = setStrProp(SheetDetails,"Sheet",'Particulars')]
		<!--link rel="onChangeToken" type="macro" href="[r: macroLinkText('RefreshCharSheet@Lib:HackMaster', 'none', 'Particulars','impersonated')]"></link-->
	};
	case "Equipment":
	{
		[r, macro("HTML_CharSheet_Equip@Lib:HackMaster"): macro.args]

		[h: SheetDetails = setStrProp(SheetDetails,"Sheet",'Equipment')]
		<!--link rel="onChangeToken" type="macro" href="[r: macroLinkText('RefreshCharSheet@Lib:HackMaster', 'none', 'Equipment','impersonated')]"></link-->
	};
	case "Combat":
	{
		[r, macro("HTML_CharSheet_Combat@Lib:HackMaster"): macro.args]

		[h: SheetDetails = setStrProp(SheetDetails,"Sheet",'Combat')]
		<!--link rel="onChangeToken" type="macro" href="[r: macroLinkText('RefreshCharSheet@Lib:HackMaster', 'none', 'Equipment','impersonated')]"></link-->
	};
	default:
	{
		[r, macro("HTML_CharSheet_Main@Lib:HackMaster"): macro.args]

		[h: SheetDetails = setStrProp(SheetDetails,"Sheet",'Main')]
		<!--link rel="onChangeToken" type="macro" href="[r: macroLinkText('RefreshCharSheet@Lib:HackMaster', 'none', 'Main','impersonated')]"></link-->
	}]
}]

[h: abort(0)]

[Dialog("Character Generator","width=480; height=350; temporary=1; input=1; noframe=0"):
{
	[r, macro("CharGenStep1HTML@Lib:HackMaster"): macro.args]
}]
[abort(0)]

[h: assert(hasImpersonated(),"ERROR: You Must Impersonate to Display a Character Sheet", 0)]
