		Takes a [r: Damage_Taken=Damage_Taken] damage wound.

		[h: Wounds = json.append(Wounds,json.fromStrProp("Sev="+Damage_Taken+";Aid=0;Rest=0;"))]
		[macro("CleanWounds@Lib:HackMaster"): ""]
	};
	{
		[h: Healing_Amount=-Damage_Taken]
		[macro("HealWound@Lib:HackMaster"): Healing_Amount]
		Is healed for [r: Healing_Amount].
	}]

	HP remaining: [r: HP]

	[h: bar.Health = HP/MaxHP]

	[h: state.Dead = if(HP <=0, 1,0)]

	[r, macro("RefreshCharSheet@Lib:HackMaster"): "Main"]
};{}]

