	{
		[H: ItemInput = ItemInput + ', " New' + curProp + ' | ' + json.get(curWound,curProp) + ' | ' + curProp + ' | TEXT | WIDTH=30" ']
	}]

	[H: ItemInput = ItemInput + ', "DeleteItem | 0 | Delete This Item | CHECK ")']
	[H: fail = eval(ItemInput)]
	[H: abort(fail)]

	[h, foreach(curProp, json.fields(curWound), ""), code:
	{
		[h: curWound = json.set(curWound,curProp,eval("New"+curProp))]
	}]

	[if(DeleteItem), code:
	{
		[h: Wounds = json.remove(Wounds,varIndex)]
		[macro("CleanWounds@Lib:HackMaster"): ""]
		Wound Removed. Healed for [r: json.get(curWound,"Sev")] HP.  HP remaining: [r: HP] 
	};
	{
		[h: Wounds = json.set(Wounds,varIndex,curWound)]
