[h: AidBonus = i_Aid + i_chkFast]
[h: HealCnt=0]
[h, foreach(curwound, Wounds, ""), code:
{
	[index=roll.count]

	[curRest=json.get(curwound,"Rest")+1+max(json.get(curwound,"Aid")-1,0)]	
	[curSev=json.get(curwound,"Sev")]

	[if(curSev<=(AidBonus)), code:
	{
		[curwound = json.set(curwound,"Sev",0)]
		[Skip=1]
	};
	{
		[Skip=0]
	}]

	[if(curSev<=(curRest+AidBonus) && Skip==0), code:
	{
		[curwound = json.set(curwound,"Sev",curSev-1)]
		[curwound=json.set(curwound,"Rest",curRest+AidBonus-curSev)]
		[HealCnt = HealCnt+1]
	};
	{
		[curwound=json.set(curwound,"Rest",curRest)]
	}]
	[Wounds = json.set(Wounds,index,curwound)]	
}]
[macro("CleanWounds@Lib:HackMaster"): ""]

