[h: GridInfo=json.get(getInfo("map"),"grid")]
[r: CellPx = json.get(GridInfo,"cell height")]
[r: UnitsPC = json.get(GridInfo,"units per cell")]

[h: DmX=0]
[h: DmY=0]
[h: sizeRed=0]
[h, switch(getSize(Dest)), code:
case "Tiny":{[EffectiveRangeMult=2][DmX=0.3*CellPx][DmX=0.3*CellPx]};
case "Small":{[EffectiveRangeMult=1.5][DmX=0.4*CellPx][DmX=0.4*CellPx]};
case "Medium":{[EffectiveRangeMult=1][DmX=0.5*CellPx][DmX=0.5*CellPx]};
case "Large":{[EffectiveRangeMult=2.0/3.0][DmX=1*CellPx][DmY=1*CellPx][sizeRed=2.5]};
case "Huge":{[EffectiveRangeMult=1.0/2.0][DmX=1.5*CellPx][DmY=1.5*CellPx][sizeRed=5]};
case "Gargantuan":{[EffectiveRangeMult=1.0/4.0][DmX=2.0*CellPx][DmY=2.0*CellPx][sizeRed=7.5]};
default: {[EffectiveRangeMult=1]}]

[h: SmX=0]
[h: SmY=0]
[h, switch(getSize(Source)), code:
case "Tiny":{[SmX=0.3*CellPx][SmY=0.3*CellPx]};
case "Small":{[SmX=0.4*CellPx][SmY=0.4*CellPx]};
case "Medium":{[SmX=0.5*CellPx][SmY=0.5*CellPx]};
case "Large":{[SmX=1*CellPx][SmY=1*CellPx][sizeRed=sizeRed+2.5]};
case "Huge":{[SmX=1.5*CellPx][SmY=1.5*CellPx][sizeRed=sizeRed+5]};
case "Gargantuan":{[SmX = 2.0*CellPx][SmY = 2.0*CellPx][sizeRed=sizeRed+7.5]};
default: {}]

[h: DistanceX = abs((getTokenX(1,Source)+SmX)-(getTokenX(1,Dest)+DmX))]
[h: DistanceY = abs((getTokenY(1,Source)+SmY)-(getTokenY(1,Dest)+DmY))]
[h: TotalDistance = sqrt(DistanceX*DistanceX+DistanceY*DistanceY)]
[h: ActualRange = round(((TotalDistance/CellPx)*UnitsPC-sizeRed),1)]
[h: SzAdjRange = round(EffectiveRangeMult*((TotalDistance/CellPx)*UnitsPC-sizeRed),1)]
[h: macro.return = strPropFromVars("ActualRange,SzAdjRange", "UNSUFFIXED")]

