[h: Skill_List = ""]

[h: Skill_Struct = master.returnItemsOfType("Skills","Skills")]

[h, foreach(curSkill,Skill_Struct,""), code:
{
	[h: loops=roll.count]

	[h, if(json.get(curSkill,"Subskill") == "(type)"), code:
	{
	};
	{
		[h: Skill_List = Skill_List + if(loops>0,",","")]
		[h: Skill_List = Skill_List + json.get(curSkill,"Name")]
	}]
}]

[h: Skill_List]

[h:status=input(		
	"junkVar|TEST|TEST|LABEL|TEXT=FALSE SPAN=TRUE",
	"Sel_Skill|"+Skill_List+"|Skill|LIST|SELECT=0 VALUE=STRING",
	"Dif|Trivial(+90), Easy(+80), Average(+40), Difficult(+0), Very Difficult(-10) |Difficulty|LIST|SELECT=3 VALUE=NUMBER",
	"Opp|0|Opposed Roll|CHECK",
	"Mods|0|Other Modifiers")]
[h:abort(status)]

[h: Selection = Dif]
[h, switch(Selection), code:
	case "0":
	{
		[Dif = +90]
	};
	case "1":
	{
		[Dif = +80]
	};
	case "2":
	{
		[Dif = +40]
	};
	case "3":
	{
		[Dif = 0]
	};
	case "4":
	{
		[Dif = -10]
	};
	default:
	{}
]

[g, foreach(Selected_Token,getSelected(),""), CODE:
{
	[g, macro("GMrollSkill@Lib:HackMaster"): "Selected_Token="+Selected_Token+"; Skill_Name="+Sel_Skill+"; Dif="+Dif+"; Opp="+Opp+"; Mods="+Mods+";"]
